import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';

import { collection, onSnapshot,doc, getDoc,docs, orderBy, query } from 'firebase/firestore';
import React, { useEffect, useState } from 'react'
import './App.css'
import { auth, db } from './firebase';
import Post from './Post'
import { Input } from '@mui/material';
import { createUserWithEmailAndPassword, onAuthStateChanged, signInWithEmailAndPassword, signOut, updateProfile} from 'firebase/auth';
import ImageUpload from './ImageUpload';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '1px solid #000',
  boxShadow: 24,
  p: 4,
};

function App() {
  const [posts, setPosts] = useState([])
  const [open, setOpen] = useState(false);
  const[opensignIn,setOpensignIn]=useState(false);
  const [username, setUsername] = useState('')
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const handlesigninopen=()=>setOpensignIn(true)
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  const handlesigninclose=()=>setOpensignIn(false)
  const [user,setUser]=useState(null)

useEffect(() => {
  onAuthStateChanged(auth, (authUser) => {
    if (authUser) {
      console.log(authUser);
      setUser(authUser)
      
    
    } else {
    setUser(null)
    }
  });
}, [])



  useEffect(() => {
    onSnapshot(
    query(collection(db, "posts"),orderBy('timestamp','desc')), 
    (snapshot) => {
      setPosts(snapshot.docs.map((doc)=>({id:doc.id,post:doc.data()})));
    });
   
  },[])
 const signUp=(e)=>{
  e.preventDefault();
createUserWithEmailAndPassword(auth, email, password)
.then((userCredential) => {
 const authuser=userCredential.user
 return updateProfile(auth.currentUser, {
  displayName:username
})
})
  .catch((error) => {
    alert(error.message)
  });
setOpen(false)
 }
 const signIn=(e)=>{
  e.preventDefault();
  signInWithEmailAndPassword(auth,email,password)
  .catch((error)=>{
    alert(error.message)
  })
  setOpensignIn(false)
 }
 
  return (
    <div className='app'>
    
      <Modal
        open={open}
        onClose={handleClose}
       
      >
        <Box sx={style}>
         <form action="" className="app_signup">
          <center>
            <img src='
       data:image/png;base64,
       'alt="" className="appImage_header" />
       </center>
        <Input
       placeholder='username'
       type='text'
       value={username}
       onChange={(e)=>{setUsername(e.target.value)}}/>

       <Input
       placeholder='email'
       type='email'
       value={email}
       onChange={(e)=>{setEmail(e.target.value)}}/>

       <Input
       placeholder='password'
       type='password'
       value={password}
       onChange={(e)=>{setPassword(e.target.value)}}/>

      <Button onClick={signUp}>Sign Up</Button>
      
      </form>  
        </Box>
         
      </Modal>
      <Modal
        open={opensignIn}
        onClose={handlesigninclose}
       
      >
        <Box sx={style}>
         <form action="" className="app_signup">
          <center>
            <img src='
       data:image/png;base64,
       'alt="" className="appImage_header" />
       </center>
     

       <Input
       placeholder='email'
       type='email'
       value={email}
       onChange={(e)=>{setEmail(e.target.value)}}/>

       <Input
       placeholder='password'
       type='password'
       value={password}
       onChange={(e)=>{setPassword(e.target.value)}}/>

      <Button onClick={signIn}>Sign In</Button>
      
      </form>  
        </Box>
         
      </Modal>
      <div className="app_header">
       <img src='
       data:image/png;base64,
       'alt="" className="appImage_header" />
       {user?(<Button onClick={()=>{signOut(auth)}}>Log Out</Button>):
        (<div className='app_logincontainer'>
           <Button onClick={handleOpen}>Sign Up</Button>
        <Button onClick={handlesigninopen}>Sign In</Button>
        </div>)
        }
      </div>
       
     {posts.map(({id,post})=>{
      return(
        <Post key={id} username={post.username} caption={post.caption} imageUrl={post.imageUrl} />
      )
     })}
       {user?.displayName?(<ImageUpload username={user.displayName}/>):(
        <h2>Sorry,please login</h2>
      )}
       
    </div>
  )
}

export default App